import React,{useState, useEffect} from 'react';
import LoadingMask from './components/LoadingMask'
import Hotels from './components/Hotels';
import Subscription from './components/Subscription';
import './App.css'

const App = () => {

  const [data, setData] = useState(null)
  const [isLoading, setIsLoading] = (false)

    const fetchData = () => {

      fetch('api/hotels')
       .then(res =>res.json())
       .then(data =>{
         setData(data)
       })
       setData(data)
      setIsLoading(false)
  }
  useEffect(() => {
    fetchData()  
  },[])

  return (
    <div className="App">
     <h1>Hotels</h1>
      <LoadingMask/>
      data.map((item, i) => <Hotels
                            item={item}
                            key={i}

      />)
      <Subscription/>
    </div>
  )
}

export default App
