import React,{useState} from 'react';

const Subscription = ({item}) => {

    const [email, setEmail] = useState('')



    const emailVal = (str) => {
        return str.includes('@') && str.includes('.')
      }

      const sendEmail = (str) => {
        setLoading(true)
        setIsShow(false)
        fetch('api/hotels', {
         method: 'POST',
         headers: {
           'Content-Type': 'application/json'   
         },
         body: {
              'email' : str
         }
      }).then(res => res.json())
        .then(data => {
              console.log(data)

       })
        .catch((error) => {
           console.error('Error:', error)

       })
               }

    return (
        <div>
            <button disabled={!emailVal(email)} onClick={() => sendEmail(email)}>{`Request more info about ${item.name}`}</button>
            <input type='text'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
            />
        </div>
    )
}

export default Subscription
